mapper.sql:
SELECT A.pId ,A.title ,A.content ,A.query ,A.parent ,A.fileYn ,A.delYn ,A.regr ,A.modr ,A.regDtime ,A.modDtime FROM ProductTbl A WHERE 1 = 1
AND A.pId = #{pId}
AND A.parent = #{parent}
AND title LIKE CONCAT('%', #{ searchWord }, '%')
AND A.delYn = 'n'

mapper.select:

SELECT ctg.ctgId, ctg.ctgNm, ctg.ctgParent, level
FROM (
    SELECT connect_by_category() AS id, @level AS level, @inclause AS inclause
    FROM (
        SELECT 
            @start_with := 0,
            @id := @start_with,
            @level := 0,
            @ctgSct := #{ctgSct},
            @inclause := ''
    ) vars, CategoryTbl
    WHERE @id IS NOT NULL
) A
LEFT JOIN CategoryTbl ctg ON ctg.ctgId = A.id
WHERE A.id IS NOT NULL

mapper.update:
UPDATE DcmtFileTbl SET pId = #{pId}, fileSct = #{fileSct}, delYn = #{delYn}, fileLoc = #{fileLoc}, fileOrgNm = #{fileOrgNm}, fileSavNm = #{fileSavNm}, regr = #{regr}, regDtime = #{regDtime}, modr = #{modr}, modDtime = #{modDtime}, fileId = fileId
AND fileId = #{fileId}
AND pId = #{pId}
